import { useState } from "react";
import "../styles/form.css";

function SignupForm() {
  // password visibility

  const [isVisible, setVisible] = useState(false);

  let visibility_icon = isVisible ? "" : " ";
  let input_type = isVisible ? "text" : "password";

  function togglePasswordVisibilty() {
    if (!isVisible) {
      setVisible(true);
    } else setVisible(false);
  }

  // input states

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [passwordConfimation, setPasswordConfirmation] = useState("");


  let handleNameInput = (name) => {
    setName(name);
  };

  let handleEmailInput = (email) => {
    setEmail(email);
  };

  let handlePasswordInput = (password) => {
    setPassword(password);
  };

  return (
    <main className="form-container">
      <form>
        {" "}
        <h1 className="form-title">Sign Up</h1>
        <div className="form-group">
          <input
            onChange={(e) => {}}
            type="text"
            name="name"
            placeholder="..."
            required
          />
          <label for="name" className="label-name">
            <span className="content-name">Name</span>
          </label>
          <div className="verification-icon-cont"></div>
          <div className="error-msg-container">
            <h6>Invalid email address</h6>
          </div>
        </div>
        <div className="form-group">
          <input
            onChange={(e) => {}}
            type="text"
            name="name"
            placeholder="..."
            required
          />
          <label for="name" className="label-name">
            <span className="content-name">Email</span>
          </label>
          <div className="verification-icon-cont"></div>
          <div className="error-msg-container">
            <h6>Invalid email address</h6>
          </div>
        </div>
        <div className="form-group">
          <input
            onChange={(e) => {
              handlePasswordInput(e.target.value);
            }}
            type={input_type}
            name="name"
            value={password}
            placeholder="..."
            required
          />
          <label for="name" className="label-name">
            <span className="content-name">Password</span>
          </label>
          <div
            onClick={() => {
              togglePasswordVisibilty();
            }}
            className="visibility-icon-cont"
          ></div>
          <div className="error-msg-container">
            <h6>invalid password</h6>
          </div>
        </div>  
      <div className="form-group">
          <input
            onChange={(e) => {
              handlePasswordInput(e.target.value);
            }}
            type={input_type}
            name="name"
            value={passwordConfimation}
            placeholder="..."
            required
          />
          <label for="name" className="label-name">
            <span className="content-name">Confirm password</span>
          </label>
          <div
            onClick={() => {
              togglePasswordVisibilty();
            }}
            className="visibility-icon-cont"
          ></div>
          <div className="error-msg-container">
            <h6>invalid password</h6>
          </div>
        </div>
      </form>
    </main>
  );
}

export default SignupForm;
