import { useState } from "react";
import "../styles/login.css"
function LoginForm() {

    // password visibility

    const[isVisible, setVisible] = useState(false)

    let visibility_icon = isVisible ? "" : " " ;
    let input_type = isVisible ? "password" : "text" ;

    function togglePasswordVisibilty(){
        if(!isVisible){
            setVisible(true);
        }
        else(
            setVisible(false)
        )
    }

    // input states

    const[name, setName] = useState('');
    const[email, setEmail] = useState('');
    const[password, setPassword] = useState('');

    let handleNameInput = (name) => {
        setName(name);
    }

    let handleEmailInput = (email) => {
        setEmail(email);
    }

    let handlePasswordInput = (password) => {
        setPassword(password);
    }




    // handle form submission




    return ( 
        <main className="form-container">
            <form>
                <h1 className="form-title">Login.</h1>
                <div className="form-group">
                    <input onChange={(e)=>{handleNameInput(e.target.value)}} type="text" name="name" autocomplete="off" value={name} required/>
                    <label for="name" className="label-name">
                        <span className="content-name">Name</span>
                    </label>
                </div>
                <div className="form-group">
                    <input onChange={(e)=>{handleEmailInput(e.target.value)}} type="email" name="name" autocomplete="off" value={email} required/>
                    <label for="name" className="label-name">
                        <span className="content-name">Email</span>
                    </label>
                </div>
                <div className="form-group">
                    <input onChange={(e)=>{handlePasswordInput(e.target.value)}} type={input_type} name="name" autocomplete="off" value={password} required/>
                    <label for="name" className="label-name">
                        <span className="content-name">Password</span>
                    </label>
                    <div 
                       onClick={()=>{
                        togglePasswordVisibilty()
                       }}
                     className="visibility-icon-cont"></div>
                </div>
                <h5 className="forgot-msg">Forgot your password?</h5>
                <button onClick={()=>{handleSubmit()}} className="form-button">
                    LOGIN
                </button>
                 <h4 className="redirect-msg">Not a member? Sign up</h4>
            </form>
        </main>
     );
}

export default LoginForm;
