import { useState } from "react";
import "../styles/login.css"
function LoginForm() {

    // password visibility

    const[isVisible, setVisible] = useState(false)

    let visibility_icon = isVisible ? "" : " " ;
    let input_type = isVisible ? "text" : "password" ;

    function togglePasswordVisibilty(){
        if(!isVisible){
            setVisible(true);
        }
        else(
            setVisible(false)
        )
    }




    // input states



    const[name, setName] = useState('');
    const[email, setEmail] = useState('');
    const[password, setPassword] = useState('');

    let handleNameInput = (name) => {
        setName(name);
    }

    let handleEmailInput = (email) => {
        setEmail(email);
    }

    let handlePasswordInput = (password) => {
        setPassword(password);
    }


    // verification icons

    const[isNameValid, setNameValid] = useState(false);
    const[isEmailValid, setEmailValid] = useState(false);
    const[isPasswordValid, setPasswordrdValid] = useState(false);


    const nameIcon = isNameValid ? "" : "" ;
    const emailIcon = isEmailValid ? "" : "" ;
    const passwordIcon = isPasswordValid ? "" : "" ;



    // handle form submission

    let handleSubmit = () => {
        console.log({
            "name": name,
            "email": email,
            "password": password
        })
    }




    return ( 
        <main className="form-container">
            <form>
                <h1 className="form-title">Login.</h1>
                <div className="form-group">
                    <input onChange={(e)=>{handleNameInput(e.target.value)}} type="text" name="name" value={name} placeholder = "..." required/>
                    <label for="name" className="label-name">
                        <span className="content-name">Name</span>
                    </label>
                    <div className="verification-icon-cont"></div>
                </div>
                <div className="form-group">
                    <input onChange={(e)=>{handleEmailInput(e.target.value)}} type="email" name="name" value={email} placeholder = "..." required/>
                    <label for="name" className="label-name">
                        <span className="content-name">Email</span>
                    </label>
                    <div className="verification-icon-cont"></div>
                </div>
                <div className="form-group">
                    <input onChange={(e)=>{handlePasswordInput(e.target.value)}} type={input_type} name="name" value={password} placeholder = "..." required/>
                    <label for="name" className="label-name">
                        <span className="content-name">Password</span>
                    </label>
                    <div 
                       onClick={()=>{
                        togglePasswordVisibilty()
                       }}
                     className="verification-icon-cont"></div>
                </div>
                <h5 className="forgot-msg">Forgot your password?</h5>
