import React, { useEffect, useState } from "react";
import "../styles/table.css";

function Table() {
  const [elements, setElements] = useState([]);
  const [elementsLowerGrid, setElementsLowerGrid] = useState([]);

  useEffect(() => {
    fetch("https://api.npoint.io/de68b6bf88b29ffe56f5")
      .then((response) => response.json())
      .then((data) => {
        setElements(data);
        console.log(data);
      });
  }, []);

  let lowerGrid = elements.filter((elem) => {
    return (
      (elem.atomicNumber > 18 && elem.atomicNumber <= 57) ||
      (elem.atomicNumber >= 72 && elem.atomicNumber <= 89) ||
      (elem.atomicNumber >= 104 && elem.atomicNumber <= 118)
    );
  });

  let lanthanides = elements.filter((elem) => { return elem.groupBlock === "lanthanoidea"; });

  let lanthanidesList = lanthanides.map((el) => {
    return(
        <div key={index} id="elementCard">
        <h6>{elem.symbol}</h6>
      </div>
    )
  })

  let lowerElementsList = lowerGrid.map((elem, index) => {
    return (
      <div key={index} id="elementCard">
        <h6>{elem.symbol}</h6>
      </div>
    );
  })

  return <div id="table">{lowerElementsList}</div>;
}

export default Table;
